try {
guardISOFormat('not-an-iso');
throw new Error('Should have thrown');
} catch (err) {
console.assert(err instanceof Error, 'Throws on invalid format');
}

import { guardBigIntRange } from './guards.mjs';
guardBigIntRange(1000000000n);
try {
guardBigIntRange(-1n);
